#!/bin/bash
#
# Deploy Windows Client Configuration via AWS SSM
# Simple, pragmatic approach using direct SSM commands
#
# Usage:
#   This script is called by ll-win-client-aws.py to deploy software
#
#   Manual usage:
#     DCV_ADMIN_PASSWORD="YourPassword" ./deploy-windows-client.sh [instance-id] [region]
#
# Required Environment Variables:
#   DCV_ADMIN_PASSWORD - Windows Administrator password (generated by ll-win-client-aws.py)
#
# Optional Software Control (set to 1 to enable, 0 to disable):
#   INSTALL_VLC=0 ./deploy-windows-client.sh          # Disable VLC
#   INSTALL_ADOBE_CC=0 ./deploy-windows-client.sh     # Disable Adobe CC
#   INSTALL_7ZIP=1 ./deploy-windows-client.sh         # Enable 7-Zip
#   INSTALL_NOTEPAD_PP=1 ./deploy-windows-client.sh   # Enable Notepad++
#
# Examples:
#   DCV_ADMIN_PASSWORD="SecPass!" ./deploy-windows-client.sh i-xxx us-east-1
#   DCV_ADMIN_PASSWORD="SecPass!" INSTALL_7ZIP=1 ./deploy-windows-client.sh i-xxx us-east-1
#
set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Configuration - Update these for your deployment
INSTANCE_ID="${1:-i-04a97c9efaa7eb3f3}"
REGION="${2:-us-east-1}"
SECRET_ARN="arn:aws:secretsmanager:us-east-1:534711626568:secret:ll-win-client/lucidlink/max.lucid-demo/credentials-1iu1Mp"
MOUNT_POINT="L:"

# DCV Administrator Password - MUST be provided via environment variable
# This password is generated and managed by ll-win-client-aws.py
if [ -z "$DCV_ADMIN_PASSWORD" ]; then
    echo -e "${RED}ERROR: DCV_ADMIN_PASSWORD environment variable is required${NC}"
    echo "This script is called by ll-win-client-aws.py which generates the password."
    echo "To run manually: DCV_ADMIN_PASSWORD=\"YourPassword\" $0 $@"
    exit 1
fi

# Optional Software Configuration (set to 1 to enable, 0 to disable)
INSTALL_VLC="${INSTALL_VLC:-1}"              # VLC Media Player (default: enabled)
INSTALL_ADOBE_CC="${INSTALL_ADOBE_CC:-1}"    # Adobe Creative Cloud (default: enabled)
INSTALL_7ZIP="${INSTALL_7ZIP:-0}"            # 7-Zip (default: disabled)
INSTALL_NOTEPAD_PP="${INSTALL_NOTEPAD_PP:-0}" # Notepad++ (default: disabled)

# Helper function to run SSM command and wait for result
run_ssm() {
    local description="$1"
    shift
    local commands=("$@")

    echo -e "${BLUE}[$(date '+%H:%M:%S')]${NC} ${description}..."

    # Build JSON array of commands
    local json_commands=$(printf '%s\n' "${commands[@]}" | jq -R . | jq -s .)

    # Send command
    CMD_ID=$(aws ssm send-command \
        --instance-ids "$INSTANCE_ID" \
        --region "$REGION" \
        --document-name "AWS-RunPowerShellScript" \
        --parameters "commands=${json_commands}" \
        --timeout-seconds 600 \
        --output text \
        --query 'Command.CommandId')

    echo -e "  ${YELLOW}Command ID:${NC} $CMD_ID"

    # Wait for completion
    sleep 10

    local max_attempts=60
    local attempt=0

    while [ $attempt -lt $max_attempts ]; do
        STATUS=$(aws ssm get-command-invocation \
            --command-id "$CMD_ID" \
            --instance-id "$INSTANCE_ID" \
            --region "$REGION" \
            --query 'Status' \
            --output text 2>/dev/null || echo "Pending")

        if [ "$STATUS" = "Success" ]; then
            echo -e "  ${GREEN}âœ“ Success${NC}"

            # Show output
            OUTPUT=$(aws ssm get-command-invocation \
                --command-id "$CMD_ID" \
                --instance-id "$INSTANCE_ID" \
                --region "$REGION" \
                --query 'StandardOutputContent' \
                --output text)

            if [ -n "$OUTPUT" ]; then
                echo "$OUTPUT" | sed 's/^/    /'
            fi

            return 0

        elif [ "$STATUS" = "Failed" ] || [ "$STATUS" = "Cancelled" ] || [ "$STATUS" = "TimedOut" ]; then
            echo -e "  ${RED}âœ— Failed (Status: $STATUS)${NC}"

            # Show error
            aws ssm get-command-invocation \
                --command-id "$CMD_ID" \
                --instance-id "$INSTANCE_ID" \
                --region "$REGION" \
                --query '[StandardOutputContent,StandardErrorContent]' \
                --output text | sed 's/^/    /'

            return 1
        fi

        # Still running
        sleep 10
        ((attempt++))
    done

    echo -e "  ${YELLOW}â± Timeout waiting for command${NC}"
    return 1
}

# Main deployment
main() {
    echo "=================================================="
    echo "  Windows Client Deployment via SSM"
    echo "=================================================="
    echo "Instance ID: $INSTANCE_ID"
    echo "Region:      $REGION"
    echo ""
    echo "Optional Software:"
    echo "  VLC Media Player:       $([ "$INSTALL_VLC" = "1" ] && echo -e "${GREEN}ENABLED${NC}" || echo -e "${YELLOW}DISABLED${NC}")"
    echo "  Adobe Creative Cloud:   $([ "$INSTALL_ADOBE_CC" = "1" ] && echo -e "${GREEN}ENABLED${NC}" || echo -e "${YELLOW}DISABLED${NC}")"
    echo "  7-Zip:                  $([ "$INSTALL_7ZIP" = "1" ] && echo -e "${GREEN}ENABLED${NC}" || echo -e "${YELLOW}DISABLED${NC}")"
    echo "  Notepad++:              $([ "$INSTALL_NOTEPAD_PP" = "1" ] && echo -e "${GREEN}ENABLED${NC}" || echo -e "${YELLOW}DISABLED${NC}")"
    echo "=================================================="
    echo

    # Step 1: Install AWS CLI
    run_ssm "Installing AWS CLI" \
        '$awsCliPath = "C:\Program Files\Amazon\AWSCLIV2\aws.exe"' \
        'if (Test-Path $awsCliPath) {' \
        '    Write-Host "AWS CLI already installed"' \
        '} else {' \
        '    Write-Host "Downloading AWS CLI..."' \
        '    $installerUrl = "https://awscli.amazonaws.com/AWSCLIV2.msi"' \
        '    $installerPath = "$env:TEMP\AWSCLIV2.msi"' \
        '    Invoke-WebRequest -Uri $installerUrl -OutFile $installerPath' \
        '    Write-Host "Installing..."' \
        '    Start-Process msiexec.exe -ArgumentList "/i $installerPath /quiet /norestart" -Wait' \
        '    Write-Host "AWS CLI installed successfully"' \
        '}'

    # Step 2: Install DCV Server and Configure Authentication
    run_ssm "Installing Amazon DCV Server (5-10 minutes)" \
        'Write-Host "Downloading DCV Server..."' \
        '$dcvUrl = "https://d1uj6qtbmh3dt5.cloudfront.net/nice-dcv-server-x64-Release.msi"' \
        '$dcvPath = "C:\Temp\dcv-server.msi"' \
        'New-Item -ItemType Directory -Path C:\Temp -Force | Out-Null' \
        'Invoke-WebRequest -Uri $dcvUrl -OutFile $dcvPath -TimeoutSec 600' \
        'Write-Host "Installing DCV Server..."' \
        'Start-Process msiexec.exe -ArgumentList "/i $dcvPath /quiet /norestart ADDLOCAL=ALL" -Wait' \
        'Write-Host "Setting Administrator password..."' \
        '$adminPassword = "'"$DCV_ADMIN_PASSWORD"'"' \
        'Set-LocalUser -Name Administrator -Password (ConvertTo-SecureString $adminPassword -AsPlainText -Force)' \
        'Write-Host "Starting DCV services..."' \
        'Start-Sleep -Seconds 10' \
        'Start-Service -Name DcvServer -ErrorAction SilentlyContinue' \
        'Start-Sleep -Seconds 10' \
        'Write-Host "Ensuring DCV session is owned by Administrator..."' \
        '# Close any existing sessions (DCV auto-creates a SYSTEM session on startup)' \
        '& "C:\Program Files\NICE\DCV\Server\bin\dcv" close-session console -ErrorAction SilentlyContinue 2>&1 | Out-Null' \
        'Start-Sleep -Seconds 2' \
        '# Create new session with Administrator owner' \
        'Write-Host "Creating DCV console session with Administrator owner..."' \
        '& "C:\Program Files\NICE\DCV\Server\bin\dcv" create-session --type=console --owner=Administrator console 2>&1' \
        'Write-Host "DCV Server configured with password: $adminPassword"'

    # Step 3: Install Chrome
    run_ssm "Installing Google Chrome" \
        'Write-Host "Downloading Chrome..."' \
        '$chromeUrl = "https://dl.google.com/chrome/install/latest/chrome_installer.exe"' \
        '$chromePath = "C:\Temp\chrome_installer.exe"' \
        'Invoke-WebRequest -Uri $chromeUrl -OutFile $chromePath -TimeoutSec 300' \
        'Write-Host "Installing Chrome..."' \
        'Start-Process $chromePath -ArgumentList "/silent /install" -Wait' \
        'Write-Host "Chrome installed successfully"'

    # ==================================================================
    # OPTIONAL SOFTWARE INSTALLATIONS
    # ==================================================================

    # Step 4: Install VLC Media Player (if enabled)
    if [ "$INSTALL_VLC" = "1" ]; then
        run_ssm "Installing VLC Media Player" \
            'Write-Host "Downloading VLC..."' \
            '$vlcUrl = "https://get.videolan.org/vlc/last/win64/vlc-3.0.20-win64.msi"' \
            '$vlcPath = "C:\Temp\vlc.msi"' \
            'Invoke-WebRequest -Uri $vlcUrl -OutFile $vlcPath -TimeoutSec 300' \
            'Write-Host "Installing VLC..."' \
            'Start-Process msiexec.exe -ArgumentList "/i `"$vlcPath`" /qn /norestart" -Wait -NoNewWindow' \
            'Write-Host "VLC installed successfully"' \
            '$vlcExe = "C:\Program Files\VideoLAN\VLC\vlc.exe"' \
            'if (Test-Path $vlcExe) { Write-Host "Verified: VLC at $vlcExe" }'
    else
        echo -e "  ${YELLOW}Skipping VLC installation (disabled)${NC}"
    fi

    # Step 5: Download Adobe Creative Cloud Desktop installer (if enabled)
    # NOTE: Adobe CC cannot be installed silently - it requires interactive login with Adobe account
    # This step downloads the installer to the Administrator's Desktop for manual installation
    if [ "$INSTALL_ADOBE_CC" = "1" ]; then
        run_ssm "Downloading Adobe Creative Cloud installer" \
            'Write-Host "Downloading Adobe Creative Cloud installer..."' \
            '$adobeUrl = "https://lucidlink-se-tools.s3.us-east-1.amazonaws.com/adobe-cc-win/Creative_Cloud_Set-Up.exe"' \
            '$desktopPath = "C:\Users\Administrator\Desktop"' \
            'New-Item -ItemType Directory -Path $desktopPath -Force | Out-Null' \
            '$installerPath = Join-Path $desktopPath "Adobe_Creative_Cloud_Installer.exe"' \
            '[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12' \
            'Invoke-WebRequest -Uri $adobeUrl -OutFile $installerPath -TimeoutSec 300 -UseBasicParsing' \
            '$fileSize = (Get-Item $installerPath).Length / 1MB' \
            'Write-Host "Downloaded Adobe CC installer to Desktop: $([math]::Round($fileSize, 2)) MB"' \
            'Write-Host "Location: $installerPath"' \
            'Write-Host "NOTE: Adobe Creative Cloud requires manual installation with Adobe account credentials"' \
            'Write-Host "Double-click the installer on the Desktop to complete installation"'
    else
        echo -e "  ${YELLOW}Skipping Adobe Creative Cloud download (disabled)${NC}"
    fi

    # Step 5b: Install 7-Zip (if enabled)
    if [ "$INSTALL_7ZIP" = "1" ]; then
        run_ssm "Installing 7-Zip" \
            'Write-Host "Downloading 7-Zip..."' \
            '$zipUrl = "https://www.7-zip.org/a/7z2301-x64.msi"' \
            '$zipPath = "C:\Temp\7zip.msi"' \
            'Invoke-WebRequest -Uri $zipUrl -OutFile $zipPath -TimeoutSec 300' \
            'Write-Host "Installing 7-Zip..."' \
            'Start-Process msiexec.exe -ArgumentList "/i `"$zipPath`" /qn /norestart" -Wait -NoNewWindow' \
            'Write-Host "7-Zip installed successfully"'
    fi

    # Step 5c: Install Notepad++ (if enabled)
    if [ "$INSTALL_NOTEPAD_PP" = "1" ]; then
        run_ssm "Installing Notepad++" \
            'Write-Host "Downloading Notepad++..."' \
            '$nppUrl = "https://github.com/notepad-plus-plus/notepad-plus-plus/releases/download/v8.6.2/npp.8.6.2.Installer.x64.exe"' \
            '$nppPath = "C:\Temp\npp-installer.exe"' \
            'Invoke-WebRequest -Uri $nppUrl -OutFile $nppPath -TimeoutSec 300' \
            'Write-Host "Installing Notepad++..."' \
            'Start-Process $nppPath -ArgumentList "/S" -Wait' \
            'Write-Host "Notepad++ installed successfully"'
    fi

    # ==================================================================
    # CORE SOFTWARE (Always Installed)
    # ==================================================================

    # Step 6: Install LucidLink (MSI version - WORKING)
    run_ssm "Installing LucidLink" \
        'Write-Host "Downloading LucidLink MSI..."' \
        '$installerUrl = "https://www.lucidlink.com/download/new-ll-latest/win/stable/"' \
        '$lucidlinkInstaller = "C:\Temp\LucidLink-Setup.msi"' \
        '& curl.exe -L -o $lucidlinkInstaller $installerUrl' \
        '$fileSize = (Get-Item $lucidlinkInstaller).Length' \
        'Write-Host "Downloaded: $([math]::Round($fileSize/1MB, 2)) MB"' \
        'Write-Host "Installing LucidLink MSI..."' \
        '$installLog = "C:\Temp\lucidlink-install.log"' \
        '$msiArgs = @("/i","`"$lucidlinkInstaller`"","/quiet","/norestart","/log","`"$installLog`"")' \
        '$process = Start-Process msiexec -Args $msiArgs -Wait -NoNewWindow -PassThru' \
        'Write-Host "MSI exit code: $($process.ExitCode)"' \
        'Start-Sleep -Seconds 10' \
        '$lucidPath = "C:\Program Files\LucidLink\bin\lucid.exe"' \
        'if (Test-Path $lucidPath) { Write-Host "LucidLink installed successfully" } else { Write-Host "WARNING: Installation may have failed" }'

    # Step 7: Configure LucidLink as Windows Service
    run_ssm "Configuring LucidLink filespace" \
        '$secretArn = "'"$SECRET_ARN"'"' \
        '$region = "'"$REGION"'"' \
        '$mountPoint = "'"$MOUNT_POINT"'"' \
        '$lucidPath = "C:\Program Files\LucidLink\bin\lucid.exe"' \
        'if (Test-Path $lucidPath) {' \
        '    Write-Host "Installing LucidLink service..."' \
        '    & $lucidPath service --install' \
        '    Start-Sleep -Seconds 3' \
        '    Write-Host "Starting LucidLink service..."' \
        '    & $lucidPath service --start' \
        '    Start-Sleep -Seconds 5' \
        '    Write-Host "Retrieving credentials from Secrets Manager..."' \
        '    $awsPath = "C:\Program Files\Amazon\AWSCLIV2\aws.exe"' \
        '    $secretJson = & $awsPath secretsmanager get-secret-value --secret-id $secretArn --region $region --query SecretString --output text' \
        '    $creds = $secretJson | ConvertFrom-Json' \
        '    Write-Host "Linking to filespace: $($creds.domain)"' \
        '    & $lucidPath link --fs $creds.domain --user $creds.username --password $creds.password --mount-point $mountPoint' \
        '    Start-Sleep -Seconds 10' \
        '    if (Test-Path $mountPoint) {' \
        '        Write-Host "SUCCESS: Filespace mounted to $mountPoint"' \
        '    } else {' \
        '        Write-Host "WARNING: Mount point not yet accessible (may need more time)"' \
        '    }' \
        '} else {' \
        '    Write-Host "ERROR: LucidLink not installed"' \
        '}'

    # Step 8: Verify DCV
    run_ssm "Verifying DCV status" \
        'Write-Host "=== DCV Services ==="' \
        'Get-Service -Name DCV* | Select-Object Name,Status,StartType | Format-Table' \
        'Write-Host ""' \
        'Write-Host "=== DCV Port Check ==="' \
        '$result = Test-NetConnection -ComputerName localhost -Port 8443 -WarningAction SilentlyContinue' \
        'if ($result.TcpTestSucceeded) {' \
        '    Write-Host "SUCCESS: DCV is listening on port 8443"' \
        '} else {' \
        '    Write-Host "WARNING: DCV port 8443 not accessible"' \
        '}'

    # Get public IP
    PUBLIC_IP=$(aws ec2 describe-instances \
        --instance-ids "$INSTANCE_ID" \
        --region "$REGION" \
        --query 'Reservations[0].Instances[0].PublicIpAddress' \
        --output text)

    echo
    echo "=================================================="
    echo -e "  ${GREEN}Deployment Complete!${NC}"
    echo "=================================================="
    echo -e "DCV URL: ${BLUE}https://${PUBLIC_IP}:8443${NC}"
    echo -e "Username: ${BLUE}Administrator${NC}"
    echo -e "Password: ${BLUE}${DCV_ADMIN_PASSWORD}${NC}"
    echo "=================================================="
    echo
    echo "Installed Software:"
    echo "  âœ“ Amazon DCV Server"
    echo "  âœ“ Google Chrome"
    echo "  âœ“ LucidLink (mounted to $MOUNT_POINT)"
    [ "$INSTALL_VLC" = "1" ] && echo "  âœ“ VLC Media Player"
    [ "$INSTALL_ADOBE_CC" = "1" ] && echo "  ðŸ“¦ Adobe Creative Cloud installer (on Desktop - requires manual install)"
    [ "$INSTALL_7ZIP" = "1" ] && echo "  âœ“ 7-Zip"
    [ "$INSTALL_NOTEPAD_PP" = "1" ] && echo "  âœ“ Notepad++"
    echo
    echo "Next steps:"
    echo "  1. Connect via DCV client or browser"
    echo "  2. Verify LucidLink mount point: $MOUNT_POINT"
    [ "$INSTALL_ADOBE_CC" = "1" ] && echo "  3. Double-click Adobe_Creative_Cloud_Installer.exe on Desktop to install"
    [ "$INSTALL_ADOBE_CC" = "1" ] && echo "     (requires Adobe account login)"
    echo "  4. Password is saved in ~/Desktop/LucidLink-DCV/PASSWORDS.txt"
}

# Run main function
main "$@"
